sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {
    const { payload = {} } = event;
    const { data = {} } = payload;
    const { output = {} } = data;
    const { sysId = null, errors = [] } = output;

    if (sysId) {
        helpers.translate('Script is successfully saved for the record').then((value) => {
            api.emit("RECORD_PAGE_TABS#ADD_NOTIFICATIONS", {
                items: [{
                    id: "alert1",
                    status: "info",
                    icon: "info-circle-outline",
                    content: value,
                    action: {
                        type: "dismiss"
                    }
                }]
            });
            helpers.modal.close();
        });
    } else {
        const scriptSaveError = await helpers.translate('Script could not be saved for the record.');
        const alertErrors = [...errors, scriptSaveError].map((message, index) => {
            return {
                id: `alert${index}`,
                status: "critical",
                icon: "info-circle-outline",
                content: message,
                action: {
                    type: "dismiss"
                }
            };
        });

        api.emit("RECORD_PAGE_TABS#ADD_NOTIFICATIONS", {
            items: alertErrors
        });
        helpers.modal.close();
    }
}
