sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    var tableName = "remediation task";
    if (api.context.props.table != "sn_vul_vulnerability") {
        tableName = "vulnerable item";
    }

    helpers.translate('Request exception for {0}.', tableName).then((value) => {
        api.setState("modalTitle", value);
    });

    helpers.translate('Once the request to defer fixing this {0} is submitted, it is sent for approval.', tableName).then((value) => {
        api.setState("modalInfoMessage", value);
    }); 
}
