sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    api.setState('configError', api.data.fetch_assessment_configuration__1.output.config_error);
    api.setState('assessment', api.data.fetch_assessment_configuration__1.output.assessment);
    api.setState('assessmentNumber', api.data.fetch_assessment_configuration__1.output.number);
    api.setState('assessmentId', api.data.fetch_assessment_configuration__1.output.asmtId);
    api.setState('ramId', api.data.fetch_assessment_configuration__1.output.ramId);

    if (!api.data.fetch_assessment_configuration__1.output.assessment && !api.data.fetch_assessment_configuration__1.output.config_error) {
        helpers.snHttp('/api/sn_risk_advanced/riskasmntapi/getAssessmentUsers?type=' + [event.elementId], {
            method: "GET",
            batch: false

        }).then(({
            response
        }) => {
            var {
                result
            } = response;
            api.setState('defaultUserList', result.users.assigneeChoices);
            api.setState('approvers', result.users.assigneeChoices);

            //Select the current user as assessor if user has GRC business user role
            if (api.data.fetch_assessment_configuration__1.output.userHasARAassessorRole) {
                var arr = [];
                var item = {};
                item.label = api.context.session.user.fullName;
                item.id = api.context.session.user.sys_id;
                item.avatarProps = {
                    "userName": api.context.session.user.userName,
                    "imageSrc": api.context.session.user.avatar + ".iix?t=small"
                };
                item.hasImage = api.context.session.user.avatar;
                if (api.data.look_up_record.result)
                    item.sublabel = api.data.look_up_record.result.email.value;
                arr.push(item);
                api.setState('assessors', arr);
                api.setState('assessor', api.context.session.user.sys_id);
            }else{
                //Check if GRC Business User Lite is installed
                if(api.data.fetch_assessment_configuration__1.output.businessUserLiteInstalled){
                    //Check if User has Business User Lite role
                    if(api.data.fetch_assessment_configuration__1.output.userHasLiteBusinessUserRole){
                        api.setState('assessors', arr);
                    }else{
                        api.setState('assessors',result.users.assigneeChoices);
                    }
                }else{
                    api.setState('assessors',result.users.assigneeChoices);    
                }
            }
        });
    }

    api.setState('pageLoaded', true);
}
