sys_ux_client_script.script:
function handler({api, event, helpers, imports}) {
    var newGroupID = event.payload.data.output;
    api.setState('newGroupSysID', newGroupID);

    helpers.timing.setTimeout(() => {
        console.log('Reloading main screen...');
            helpers.screen.updateStatus({
            hasUpdate: true,
            status: 'updated',
            screenParams: {
                table: 'sn_vul_vulnerability',
                sysId: api.context.props.sysId
            }
        });
    }, 2000);
    helpers.modal.close();

    helpers.navigate.to('record', {
        table: 'sn_vul_vulnerability',
        sysId: newGroupID
    });
}
