sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    helpers.translate("Select the option below to add all the CIs with active VIs from this remediation task to the selected change request. The remediation task transitions to the ‘Awaiting Implementation’ state. This action is only available for change requests in the 'New' state.").then((value) => {
        api.setState("addToExistingChange", value);
    });
}
