sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {any} params.imports
  */
function evaluateProperty({api}) { 
  if(api.state.risk && api.state.ram && api.state.assessorType && api.state.approverType && api.state.daysToOverdue){
    if(api.state.assessorType=="user" || api.state.assessorType=="group"){

      if(api.state.assessor){

        if(api.state.approverType=="user" || api.state.approverType=="group"){
          if(api.state.approver){
            return false;
          }else{
            return true;
          }
        }

        return false;
      }else{
        return true;
      }

    }else{

      if (api.state.approverType == "user" || api.state.approverType == "group") {  
        if (api.state.approver) {
          return false;
        } else {
          return true;
        }
      }

      return false; 
    }
  }
	return true;
}
