sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    helpers.snHttp('/api/sn_risk_advanced/riskasmntapi/createAsmtForAnyObject', {
        method: "POST",
        body: {
            table: api.context.props.table,
            recordId: api.context.props.sysId,
            assessorId: api.state.assessor,
            approverId: api.state.approver
        },
        batch: false

    }).then(({
        response
    }) => {
        if (response.result.asmtNumber && !response.result.errorMsg) {
            helpers
                .translate('Risk assessment created successfully ')
                .then((val) => {
                    api.setState('successMessage', val);
                });
        } else {
            api.setState('successMessage', response.result.errorMsg);
        }
        api.setState('assessmentNumber', response.result.asmtNumber);
        api.setState('assessmentId', response.result.asmtSysId);
    });
}
