sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    var info = event.payload.data.output.alertMessages.success;
    var error = event.payload.data.output.alertMessages.error;
    var changeLinkLabel;
    var changeLinkUrl;

    function getChangeLinkData(errorMessage) {
        if (errorMessage.includes("change request ") || errorMessage.includes("Change request ")) {
            changeLinkLabel = "View change";
            changeLinkUrl = api.state.changeRequestLink;
        } else {
            changeLinkLabel = null;
            changeLinkUrl = null;
        }
    }

    info.forEach(function(element, index) {
        getChangeLinkData(element);
        api.emit('ADD_NOTIFICATIONS', {
            items: [{
                "id": "associate-change-alert-info" + index,
                "status": "info",
                "icon": "info-circle-outline",
                "content": element,
                "textLinkProps": {
                    "label": changeLinkLabel,
                    "href": changeLinkUrl
                },
                "action": {
                    "type": "dismiss"
                }
            }]
        });
    });

    error.forEach(function(element, index) {
        getChangeLinkData(element);
        api.emit('ADD_NOTIFICATIONS', {
            items: [{
                "id": "associate-change-alert-error" + index,
                "status": "critical",
                "icon": "info-circle-outline",
                "content": element,
                "textLinkProps": {
                    "label": changeLinkLabel,
                    "href": changeLinkUrl
                },
                "action": {
                    "type": "dismiss"
                }
            }]
        });
    });

    helpers.modal.close();
}
