sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {

    const {
        routeMapping,
        buildDefaultRoutePayload
    } = imports["global.recordRoutesMapping"]();

    var route = 'record';
    var targetRoute = '';
    event.payload={fields:{"table":"sn_risk_advanced_risk_assessment_instance","sysId":api.state.assessmentId}};
    const defaultRoutePayload = buildDefaultRoutePayload(route, targetRoute, event);
    const routeMap = api.data.dynamic_routing_1.routeMap;
    const navigationPayload = routeMapping(routeMap, defaultRoutePayload, event);

    helpers.modal.close();
    const {
        emit
    } = api;
    emit('NAV_ITEM_SELECTED', navigationPayload);
}
