sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    var infoValid = true;
    var addInfoFieldvalue = event.payload.fieldValue;
    if (event.elementId === "information_field") {
        api.setState("addInformation", event.payload.fieldValue);
    }
    infoValid = !(!addInfoFieldvalue || 0 === addInfoFieldvalue.length || addInfoFieldvalue === null);
    api.setState("submitDisabled", !infoValid);
}
