sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    if(api.data.get_risk_from_source_record__1.output.status==200){
        if(api.data.get_risk_from_source_record__1.output.risk){
            api.setState("selectedRisk", api.data.get_risk_from_source_record__1.output.risk);
            api.setState("risk",api.data.get_risk_from_source_record__1.output.risk);
            api.data.get_primary_ram_for_risk_1.refresh();
            //filter rams based on risk
            api.data.filter_ram_list_based_on_risk__1.refresh();

        }
    }else{
        if(api.data.get_risk_from_source_record__1.output.status==0){
            api.setState('errorMessage', api.data.get_risk_from_source_record__1.output.errorMsg);
        }
    }
    api.emit("NOW_UXF_PAGE#SET_LOADING_STATE", {
        id: "non_blocking",
        loading: false,
        nonBlocking: true
    });
}
