sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    var dateValid = true;
    var infoValid = true;
    var reasonValid = true;
    var disableSubmitButton = true;
    var arrayOfReasonValues = [2, 21, 23, 25, 26];
    var reasonKey = api.state.reason;

    //Validate reason and Add. Info.
    reasonValid = arrayOfReasonValues.includes(parseInt(reasonKey));
    infoValid = !(!api.state.addInformation || 0 === api.state.addInformation.length || api.state.addInformation === null);

    // Validate Date
    var currentYear = new Date().getFullYear();
    var currentMonth = pad(new Date().getMonth() + 1);
    var currentDay = new Date().getDate();
    var currentDate = currentYear + '-' + currentMonth + '-' + currentDay;
    function pad(n){return n<10 ? '0'+n : n};
    var untilDateValid = Date.parse(api.state.untilDate);
    dateValid = new Date(currentDate).valueOf() < new Date(api.state.untilDate).valueOf();

    if (dateValid && reasonValid && infoValid) {
        api.setState("submitDisabled", false);
    } else {
        api.setState("submitDisabled", true);
    }
}
