sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    helpers.translate('Exception request submitted.').then((value) => {
        api.emit('ADD_NOTIFICATIONS', {
            items: [{
                "id": "alert-succeded-to-resolve",
                "status": "info",
                "icon": "info-circle-outline",
                "content": value,
                "action": {
                    "type": "dismiss"
                }
            }]
        });
    });

    api.emit("SN_FORM_DATA_CONNECTED#RELOAD");

    helpers.modal.close();
}
