sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    helpers.snHttp('/api/sn_risk_advanced/riskasmntapi/getRiskChoices', {
        method: "GET",
        batch: false

    }).then(({
        response
    }) => {
        var {
            result
        } = response;
        api.setState('defaultRiskList', result.risks.riskChoices);
        api.setState('risksList', result.risks.riskChoices);        
    });

    helpers.snHttp('/api/sn_risk_advanced/riskasmntapi/getRiskAssessmentMethodologyChoices', {
        method: "GET",
        batch: false

    }).then(({
        response
    }) => {
        var {
            result
        } = response;
        api.setState('defaultRamList', result.rams.ramChoices);
        api.setState('ramsList', result.rams.ramChoices);        
    });

    api.setState('assessorType',"risk_owner");
    api.setState('assessor','');
    api.setState('hideAssessor',true);

    api.setState('approverType',"entity_owner");
    api.setState('approver','');
    api.setState('hideApprover',true);
}
