sys_ux_client_script.script:
/**
 * Reset node map to initial state
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
async function handler({ api, event, helpers, imports }) {
    const { name } = event;
    const requestTime = Date.now();
    const requestFailed = name === 'DATA_FETCH_FAILED';
    const fullSectorNodes = [];
    const notifications = [];
    const rootNodes = [];
    let LOADING_LABEL;
    let NO_RELATIONSHIPS;
    if (requestFailed) {
        const NO_DATA_MESSAGE = await helpers.translate(
            'Gathering relationship data failed. Try again.'
        );
        NO_RELATIONSHIPS = await helpers.translate('No relationships');
        fullSectorNodes.push({
            count: 0,
            isEmptyState: true,
            emptyStateContent: NO_RELATIONSHIPS,
            id: 'bottom_empty_state_node'
        });
        notifications.push({
            status: 'critical',
            message: NO_DATA_MESSAGE,
            icon: 'circle-info',
            action: {
                type: 'dismiss'
            }
        });
    } else {
        LOADING_LABEL = await helpers.translate(
            'Gathering relationship data...'
        );
        rootNodes.push({
            id: 'init_root_empty_state_node',
            isRoot: true,
            isLoading: true,
            loadingLabel: LOADING_LABEL
        });
    }

    api.setState('dataNavRequestTime', requestTime);
    api.setState('dataNavNotifications', notifications);
    api.setState('nodemapNodeData', [{
            sector: {
                name: 'top-sector',
                range: [0, 0],
                offset: 0,
                startPosition: 0,
                isHidden: true
            },
            nodes: []
        },
        {
            sector: {
                name: 'start-sector',
                range: [0, 0],
                offset: 0,
                startPosition: 0,
                isHidden: true
            },
            nodes: []
        },
        {
            sector: {
                name: 'root-sector',
                isRoot: true
            },
            nodes: rootNodes
        },
        {
            sector: {
                name: 'end-sector',
                range: [0, 0],
                offset: 0,
                startPosition: 0,
                isHidden: true
            },
            nodes: []
        },
        {
            sector: {
                name: 'bottom-sector',
                range: [0, 180],
                offset: 0,
                startPosition: 90,
                modifier: '-full-sector',
                isEmptyState: true
            },
            nodes: fullSectorNodes
        }
    ]);
}
