sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    helpers.snHttp('/api/sn_risk_advanced/riskasmntapi/triggerRiskAssessment', {
        method: "POST",
        body: {
            risk: api.state.risk,
            ram: api.state.ram,
            assessor: api.state.assessor,
            approver: api.state.approver,
            assessorType: api.state.assessorType,
            approverType: api.state.approverType,
            daysToOverdue: api.state.daysToOverdue

        },
        batch: false

    }).then(({
        response
    }) => {
        if (!response.result.errorMsg) {
            if (response.result.status == 201) {
                helpers
                    .translate("Risk assessment created successfully ")
                    .then((val) => {
                        api.setState('successMessage', val);
                    });
                if (api.context.props.table == "sn_risk_risk") {
                    api.emit("GFORM#RELOAD");
                }
            } else {
                api.setState('successMessage', response.result.msg);
            }
            api.setState('assessmentNumber', response.result.asmtNumber);
            api.setState('assessmentId', response.result.asmtId);
        } else {
            api.setState('errorMessage', response.result.errorMsg);
        }
    });

}
