sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    api.setState("remTaskState", event.payload.data.output.rtState);
    api.setState("changeRequestState", event.payload.data.output.crState);
    api.setState("changeRequestLink", event.payload.data.output.crLink);
    if (event.payload.data.output.crState === "-5") { // -5 New change request state
        api.setState("showAddCIs", false);
    }
}
