sys_ux_client_script.script:
function handler({api, event, helpers, imports}) {
    var dateRegex = /^\d{4}-\d{2}-\d{2}$/;
    var validPayloadDate;
    if (event.elementId === "date_input_text") {
        validPayloadDate = (event.payload.fieldValue.match(dateRegex)) ? "" + event.payload.fieldValue : "";
    } else {
        validPayloadDate = event.payload.value;
    }
    var currentYear = new Date().getFullYear();
    var currentMonth = pad(new Date().getMonth() + 1);
    var currentDay = pad(new Date().getDate());
    var currentDate = currentYear + '-' + currentMonth + '-' + currentDay;
    function pad(n){return n<10 ? '0'+n : n;};

    if (new Date(currentDate).valueOf() < new Date(validPayloadDate).valueOf()) {
        api.setState("plannedEndDate", validPayloadDate);
        api.setState("errorMessage", "");
    } else if (new Date(currentDate).valueOf() === new Date(validPayloadDate).valueOf()) {
        if (new Date(api.state.untilDate).valueOf() < new Date(currentDate).valueOf()) {
            helpers.translate("There was an error with your submission. Error message: Planned end date must be in the future.").then((value) => {
               api.setState("errorMessage", value);
            });
        }
    } else {
        api.setState("plannedEndDate", validPayloadDate);
        helpers.translate("There was an error with your submission. Error message: Planned end date must be in the future.").then((value) => {
            api.setState("errorMessage", value);
        });
    }   
}
