sys_ux_client_script.script:
/**
 * Update node data with new values
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({ api, event, helpers, imports }) {
  const { data_nav_config } = api.data;
  const { output = {} } = data_nav_config;
  const { rootTable = "", nodeData, notification_messages = [] } = output;
  let filterCondition = "active=true";

  // calculate time diff
  const { animationDuration = 1000, dataNavRequestTime: requestTime = 0 } =
    api.state;
  const responseTime = Date.now();
  const requestTimeDiff = responseTime - requestTime;
  const animationDelay =
    requestTimeDiff < animationDuration
      ? animationDuration - requestTimeDiff
      : 0;
  helpers.timing.setTimeout(() => {
    if (rootTable) {
      filterCondition += `^table=${rootTable}`;
      api.setState("configViewsConditions", filterCondition);
    }

    api.setState("requestTime", 0);
    api.setState("nodemapNodeData", nodeData);
    api.setState("dataNavNotifications", notification_messages);
  }, animationDelay);
}
