sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const cloudWorkloadId = api.state.cloudWorkloadId;

    console.log(cloudWorkloadId);

    var demand_cloud_introduction_for_new_teams = api.state.demand_cloud_introduction_for_new_teams;
    console.log(demand_cloud_introduction_for_new_teams);
    if (!demand_cloud_introduction_for_new_teams) {
        demand_cloud_introduction_for_new_teams = api.data.look_up_cloud_workload.demand_cloud_introduction_for_new_teams.value;
    }

    const updateQuery = `demand_cloud_introduction_for_new_teams=${demand_cloud_introduction_for_new_teams}`;

    api.data.update_cloud_workload.execute({
        "table": "x_asmln_cloud_wo_1_cloud_workload_workflow",
        "recordId": cloudWorkloadId,
        "updateQuery": updateQuery
    });
}
