sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    let visualizationGroupby = JSON.parse(JSON.stringify(api.state.visualizationGroupby));
    visualizationGroupby[0].groupBy[0].groupByField = event.payload.item.id;
    api.setState('selectedGroupbyInDropdown', [event.payload.item.id]);
    api.setState('visualizationGroupby', visualizationGroupby);
}
